mapper.update:
update dingdan
 set state = #{state,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select d.id, d.s_id, d.u_id, d.number, d.t_price, d.state, d.date,s.sname,s.price,u.u_name from dingdan d left join shangpin s on d.s_id=s.id LEFT JOIN user u on d.u_id = u.id
and s.sname=#{sname}
and u.u_name=#{uname}
order by d.id desc
